import React from "react";
import ReactDOM from "react-dom/client";
import { ReactKeycloakProvider } from "@react-keycloak/web";
import keycloak from "./keycloak";
import App from "./App";

// âœ… Load token & refresh token from storage
const token = localStorage.getItem("token") || sessionStorage.getItem("token");
const refreshToken = localStorage.getItem("refreshToken") || sessionStorage.getItem("refreshToken");

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <ReactKeycloakProvider
    authClient={keycloak}
    initOptions={{
      onLoad: "check-sso",
      silentCheckSsoRedirectUri: window.location.origin + "/silent-check-sso.html",
      checkLoginIframe: true,
      token,   // âœ… Pass stored token
      refreshToken,  // âœ… Pass stored refresh token
    }}
    onEvent={(event, error) => {
      console.log("ðŸŸ¡ Keycloak Event:", event);
      if (event === "onAuthSuccess") {
        console.log("âœ… Keycloak authentication successful!");
      }
      if (event === "onAuthError") {
        console.error("ðŸ”´ Keycloak authentication error:", error);
      }
    }}
  >
    <App />
  </ReactKeycloakProvider>
);
