import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { useKeycloak } from "@react-keycloak/web";
import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Signup from "./pages/SignUp";
import ForgotPassword from "./pages/ForgotPassword";
import Dashboard from "./pages/Dashboard";

const ProtectedRoute = ({ children }) => {
  const { keycloak, initialized } = useKeycloak();
  const token = localStorage.getItem("token") || sessionStorage.getItem("token");

  console.log("ðŸŸ¡ Keycloak Initialized:", initialized);
  console.log("ðŸŸ¡ Keycloak Authenticated:", keycloak.authenticated);
  console.log("ðŸŸ¡ Token Found:", token);

  if (!initialized) {
    console.log("ðŸŸ¡ Waiting for Keycloak to initialize...");
    return <div>Loading Keycloak...</div>;
  }

  // âœ… If Keycloak is not authenticated but a token exists, try refreshing it
  if (!keycloak.authenticated && token) {
    console.log("ðŸŸ¡ Token exists but Keycloak is not authenticated. Trying refresh...");

    keycloak
      .updateToken(30)
      .then(() => {
        console.log("âœ… Keycloak session refreshed!");
        window.location.reload();
      })
      .catch((error) => {
        console.error("ðŸ”´ Failed to refresh Keycloak session:", error);
        console.log("ðŸ”´ Keeping the user logged in but requiring re-authentication later.");
      });

    return <div>Refreshing session...</div>;
  }

  // âœ… If still not authenticated and no token, redirect to login
  if (!keycloak.authenticated && !token) {
    console.log("ðŸ”´ No token found. Redirecting to login...");
    return <Navigate to="/login" />;
  }

  console.log("âœ… User is authenticated. Showing dashboard.");
  return children;
};






function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
