//short api
const login = async (email, password, rememberMe) => {
  try {
      console.log("ðŸ”¹ Attempting Login for:", email);

      // ðŸ”¹ Send login request to your backend
      const response = await axios.post("http://localhost:5000/api/auth/login", {
          email,
          password,
      });

      console.log("âœ… Backend Login Response:", response.data); // Log the response

      if (!response.data.token) {
          console.error("ðŸ”´ No Token in Response:", response.data);
          throw new Error("Login failed, no token received.");
      }

      const { token, user } = response.data;

      // âœ… Store tokens securely
      if (rememberMe) {
          localStorage.setItem("token", token);
      } else {
          sessionStorage.setItem("token", token);
      }

      // âœ… Redirect to dashboard after login
      window.location.href = "/dashboard"; 

      return { token, user };
  } catch (error) {
      console.error("ðŸ”´ Login failed:", error.response?.data || error.message);
      throw error;
  }
};




  // ðŸ”¹ **Signup (Calls Your Backend, Not Keycloak Directly)**
  const signup = async (email, password) => {
    try {
        const response = await axios.post("http://localhost:5000/api/auth/signup", {
            email,
            password,
        });

        console.log("âœ… Signup successful:", response.data);
        return response.data;
    } catch (error) {
        console.error("ðŸ”´ Signup failed:", error.response?.data || error.message);
        throw error;
    }
};


  // ðŸ”¹ **Forgot Password (Calls Your Backend)**
  const forgotPassword = async (email) => {
    try {
      await axios.post(`${BACKEND_API_URL}/forgot-password`, { email });
      return "Password reset email sent!";
    } catch (error) {
      console.error("Forgot password failed:", error);
      throw error;
    }
  };

  // ðŸ”¹ **Logout from Keycloak**
  const logout = async () => {
    try {
      const refreshToken = localStorage.getItem("refreshToken") || sessionStorage.getItem("refreshToken");
  
      if (!refreshToken) {
        console.log("ðŸ”¹ No refresh token found. Clearing local session.");
        localStorage.removeItem("token");
        localStorage.removeItem("refreshToken");
        sessionStorage.removeItem("token");
        sessionStorage.removeItem("refreshToken");
        window.location.href = "/login";
        return;
      }
  
      console.log("ðŸ”¹ Logging out user via backend...");
  
      await axios.post("http://localhost:5000/api/auth/logout", { refreshToken });
  
      // âœ… Clear session after logout
      localStorage.removeItem("token");
      localStorage.removeItem("refreshToken");
      sessionStorage.removeItem("token");
      sessionStorage.removeItem("refreshToken");
  
      window.location.href = "/login"; // Redirect after logout
    } catch (error) {
      console.error("ðŸ”´ Logout failed:", error);
      window.location.href = "/login"; // Ensure user is logged out even if backend fails
    }
  };
  

  return { login, signup, forgotPassword, logout, keycloak };
};
