import React from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { useKeycloak } from "@react-keycloak/web";
import Navbar from "./components/Navbar";
import Login from "./pages/Login";
import Signup from "./pages/SignUp";
import ForgotPassword from "./pages/ForgotPassword";
import Dashboard from "./pages/Dashboard";

const ProtectedRoute = ({ children }) => {
  const { keycloak, initialized } = useKeycloak();
  const token = localStorage.getItem("token") || sessionStorage.getItem("token");

  console.log("ðŸŸ¡ Keycloak Initialized:", initialized);
  console.log("ðŸŸ¡ Keycloak Authenticated:", keycloak.authenticated);
  console.log("ðŸŸ¡ Token Found:", token);

  // âœ… If Keycloak is still initializing, don't render anything yet
  if (!initialized) {
    console.log("ðŸŸ¡ Waiting for Keycloak to initialize...");
    return <div>Loading...</div>; // Prevents redirecting too early
  }

  // âœ… If user is not authenticated and no token is found, redirect to login
  if (!keycloak.authenticated && !token) {
    console.log("ðŸ”´ Not authenticated. Redirecting to login...");
    return <Navigate to="/login" />;
  }

  console.log("âœ… User is authenticated. Showing dashboard.");
  return children;
};




function App() {
  return (
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/forgot-password" element={<ForgotPassword />} />
        <Route
          path="/dashboard"
          element={
            <ProtectedRoute>
              <Dashboard />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
